import React, { useState, useEffect } from 'react';

function App() {
  const [hours, setHours] = useState(0);
  const [minutes, setMinutes] = useState(0);
  const [seconds, setSeconds] = useState(0);
  const [timeLeft, setTimeLeft] = useState(0); // 剩餘時間（秒）
  const [isActive, setIsActive] = useState(false);

  // 倒數計時邏輯
  useEffect(() => {
    let timer = null;
    if (isActive && timeLeft > 0) {
      timer = setInterval(() => {
        setTimeLeft((prevTime) => prevTime - 1);
      }, 1000);
    } else if (timeLeft === 0) {
      setIsActive(false);
    }
    return () => clearInterval(timer);
  }, [isActive, timeLeft]);

  // 計算倒數時間
  const calculateTotalSeconds = () => {
    return parseInt(hours) * 3600 + parseInt(minutes) * 60 + parseInt(seconds);
  };

  // 開始或暫停倒數
  const toggleTimer = () => {
    if (!isActive) {
      setTimeLeft(calculateTotalSeconds());
    }
    setIsActive(!isActive);
  };

  // 重置倒數時間
  const resetTimer = () => {
    setIsActive(false);
    setTimeLeft(calculateTotalSeconds());
  };

  // 處理小時輸入變化
  const handleHoursChange = (e) => {
    setHours(e.target.value); // 即時更新輸入值，但不進行驗證
  };

  // 當小時輸入框失去焦點或完成輸入時進行驗證
  const validateHours = () => {
    let value = Number(hours);
    if (value < 0 || value > 23) {
      alert('Hours should be between 0 and 23');
      setHours(0); // 超出範圍時設置為0
    }
  };

  // 處理分鐘輸入變化
  const handleMinutesChange = (e) => {
    setMinutes(e.target.value); // 即時更新輸入值，但不進行驗證
  };

  // 當分鐘輸入框失去焦點或完成輸入時進行驗證
  const validateMinutes = () => {
    let value = Number(minutes);
    if (value < 0 || value > 59) {
      alert('Minutes should be between 0 and 59');
      setMinutes(0); // 超出範圍時設置為0
    }
  };

  // 處理秒數輸入變化
  const handleSecondsChange = (e) => {
    setSeconds(e.target.value); // 即時更新輸入值，但不進行驗證
  };

  // 當秒數輸入框失去焦點或完成輸入時進行驗證
  const validateSeconds = () => {
    let value = Number(seconds);
    if (value < 0 || value > 59) {
      alert('Seconds should be between 0 and 59');
      setSeconds(0); // 超出範圍時設置為0
    }
  };

  // 顯示剩餘時間
  const formatTimeLeft = () => {
    const hrs = Math.floor(timeLeft / 3600);
    const mins = Math.floor((timeLeft % 3600) / 60);
    const secs = timeLeft % 60;
    return `${hrs.toString().padStart(2, '0')}:${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="App">
      <h1>Countdown Timer</h1>
      <div>
        <label>Hours: </label>
        <input 
          type="number" 
          value={hours} 
          onChange={handleHoursChange} 
          onBlur={validateHours} // 輸入完成後進行驗證
          disabled={isActive}
        />
        <label>Minutes: </label>
        <input 
          type="number" 
          value={minutes} 
          onChange={handleMinutesChange} 
          onBlur={validateMinutes} // 輸入完成後進行驗證
          disabled={isActive}
        />
        <label>Seconds: </label>
        <input 
          type="number" 
          value={seconds} 
          onChange={handleSecondsChange} 
          onBlur={validateSeconds} // 輸入完成後進行驗證
          disabled={isActive}
        />
      </div>
      <div className="time">{formatTimeLeft()}</div>
      <button onClick={toggleTimer}>
        {isActive ? 'Pause' : 'Start'}
      </button>
      <button onClick={resetTimer}>Reset</button>
    </div>
  );
}

export default App;
