import React, { useState } from 'react';

function ChoiceMaker() {
  const [options, setOptions] = useState('');
  const [choices, setChoices] = useState([]);
  const [selectedChoice, setSelectedChoice] = useState('');

  // 更新選項列表
  const handleInputChange = (e) => {
    setOptions(e.target.value);
  };

  // 解析選項並存入陣列
  const handleSetChoices = () => {
    const choicesArray = options.split(',').map(option => option.trim()).filter(option => option !== '');
    setChoices(choicesArray);
    setSelectedChoice(''); // 清空已選擇的選項
  };

  // 隨機選擇選項
  const handleMakeChoice = () => {
    if (choices.length > 0) {
      const randomIndex = Math.floor(Math.random() * choices.length);
      setSelectedChoice(choices[randomIndex]);
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h1>選擇器</h1>
      <input 
        type="text" 
        placeholder="請輸入選項，用逗號分隔" 
        value={options} 
        onChange={handleInputChange} 
        style={{ width: '300px', padding: '10px' }}
      />
      <br /><br />
      <button onClick={handleSetChoices} style={{ padding: '10px 20px' }}>設定選項</button>
      <br /><br />
      {choices.length > 0 && <button onClick={handleMakeChoice} style={{ padding: '10px 20px' }}>做選擇</button>}
      <br /><br />
      {selectedChoice && <h2>選擇結果: {selectedChoice}</h2>}
    </div>
  );
}

export default ChoiceMaker;
