import React, { useState } from 'react';
import './App.css';

function ChoiceMaker() {
  const [input, setInput] = useState('');
  const [choices, setChoices] = useState([]);
  const [selectedChoice, setSelectedChoice] = useState('');
  const [history, setHistory] = useState([]);  // 新增歷史紀錄

  // 當輸入框改變時更新值
  const handleInputChange = (e) => {
    setInput(e.target.value);
  };

  // 當按下Enter時，將當前輸入加入選項
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && input.trim() !== '') {
      if (!choices.includes(input.trim())) {
        setChoices([...choices, input.trim()]);  // 添加新選項
        setInput('');  // 清空輸入框
      } else {
        alert('此選項已經存在');
      }
    }
  };

  // 刪除指定選項
  const handleDeleteChoice = (indexToDelete) => {
    setChoices(choices.filter((_, index) => index !== indexToDelete));
  };

  // 隨機選擇選項並保存至歷史紀錄，且清空選項以便重新開始
  const handleMakeChoice = () => {
    if (choices.length > 0) {
      const randomIndex = Math.floor(Math.random() * choices.length);
      const choice = choices[randomIndex];
      setSelectedChoice(choice);
      setHistory([...history, choice]);  // 保存選擇到歷史紀錄
      setChoices([]);  // 自動清空選項，使用者可以直接輸入下一輪
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
    <div class="container">
      <h1>隨機選擇器</h1>
      <p class="description">輸入選項，然後點擊“選擇”按鈕來隨機選擇一個選項！</p>
      <input type="text" id="optionInput" placeholder="輸入選項" />
      <div class="button-container">
      <button class="select-button" onclick="selectOption()">選擇</button>
      <button class="clear-button" onclick="clearOptions()">清除</button>
    </div>
    <div class="result" id="result"></div>
  </div>
      <input 
        type="text" 
        placeholder="請輸入選項並按下 Enter" 
        value={input} 
        onChange={handleInputChange} 
        onKeyPress={handleKeyPress} 
        style={{ width: '300px', padding: '10px' }}
      />
      <br /><br />
      <ul>
        {choices.map((choice, index) => (
          <li key={index}>
            {choice} 
            <button onClick={() => handleDeleteChoice(index)} style={{ marginLeft: '10px' }}>刪除</button>
          </li>
        ))}
      </ul>
      {choices.length > 0 && (
        <>
          <button onClick={handleMakeChoice} style={{ padding: '10px 20px' }}>做選擇</button>
        </>
      )}
      <br /><br />
      {selectedChoice && <h2>選擇結果: {selectedChoice}</h2>}

      {/* 顯示歷史紀錄 */}
      {history.length > 0 && (
        <div style={{ marginTop: '30px' }}>
          <h3>歷史紀錄：</h3>
          <ul>
            {history.map((item, index) => (
              <li key={index}>{item}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default ChoiceMaker;
