import React, { useState, useEffect } from 'react';

function CountdownTimer() {
  const [targetDate, setTargetDate] = useState('');
  const [timeRemaining, setTimeRemaining] = useState({
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0,
  });

  const calculateTimeRemaining = () => {
    const difference = new Date(targetDate) - new Date();
    let timeRemaining = {};

    if (difference > 0) {
      timeRemaining = {
        days: Math.floor(difference / (1000 * 60 * 60 * 24)),
        hours: Math.floor((difference / (1000 * 60 * 60)) % 24),
        minutes: Math.floor((difference / 1000 / 60) % 60),
        seconds: Math.floor((difference / 1000) % 60),
      };
    }

    return timeRemaining;
  };

  const handleTargetDateChange = (event) => {
    setTargetDate(event.target.value);
  };

  useEffect(() => {
    if (targetDate) {
      const timer = setTimeout(() => {
        setTimeRemaining(calculateTimeRemaining());
      }, 1000);

      return () => clearTimeout(timer);
    }
  }, [targetDate, timeRemaining]); 

  return (
    <div>
      <h1>倒數計時器</h1>
      <div>
        <label htmlFor="target-date">目標日期和時間：</label>
        <input
          type="datetime-local"
          id="target-date"
          value={targetDate}
          onChange={handleTargetDateChange}
        />
      </div>
      <div>
        <span>{timeRemaining.days} 天 </span>
        <span>{timeRemaining.hours} 小時 </span>
        <span>{timeRemaining.minutes} 分鐘 </span>
        <span>{timeRemaining.seconds} 秒</span>
      </div>
    </div>
  );
}

export default CountdownTimer;
