import React, { useState } from 'react';

function ChoiceMaker() {
  const [input, setInput] = useState('');
  const [choices, setChoices] = useState([]);
  const [selectedChoice, setSelectedChoice] = useState('');

  // 當輸入框改變時更新值
  const handleInputChange = (e) => {
    setInput(e.target.value);
  };

  // 當按下Enter時，將當前輸入加入選項
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && input.trim() !== '') {
      if (!choices.includes(input.trim())) {
        setChoices([...choices, input.trim()]);  // 添加新選項
        setInput('');  // 清空輸入框
      } else {
        alert('此選項已經存在');
      }
    }
  };

  // 刪除指定選項
  const handleDeleteChoice = (indexToDelete) => {
    setChoices(choices.filter((_, index) => index !== indexToDelete));
  };

  // 清空所有選項
  const handleClearChoices = () => {
    setChoices([]);
    setSelectedChoice('');
  };

  // 隨機選擇選項
  const handleMakeChoice = () => {
    if (choices.length > 0) {
      const randomIndex = Math.floor(Math.random() * choices.length);
      setSelectedChoice(choices[randomIndex]);
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h1>選擇器</h1>
      <input 
        type="text" 
        placeholder="請輸入選項並按下 Enter" 
        value={input} 
        onChange={handleInputChange} 
        onKeyPress={handleKeyPress} 
        style={{ width: '300px', padding: '10px' }}
      />
      <br /><br />
      <ul>
        {choices.map((choice, index) => (
          <li key={index}>
            {choice} 
            <button onClick={() => handleDeleteChoice(index)} style={{ marginLeft: '10px' }}>刪除</button>
          </li>
        ))}
      </ul>
      {choices.length > 0 && (
        <>
          <button onClick={handleMakeChoice} style={{ padding: '10px 20px' }}>做選擇</button>
          <button onClick={handleClearChoices} style={{ padding: '10px 20px', marginLeft: '10px' }}>清空選項</button>
        </>
      )}
      <br /><br />
      {selectedChoice && <h2>選擇結果: {selectedChoice}</h2>}
    </div>
  );
}

export default ChoiceMaker;
