import React, { useState, useEffect, useRef } from 'react';
import './App.css';

function App() {
  return (
    <div className="App">
      <h1>倒數計時器</h1>

      {/* 顯示剩餘時間 */}
      <div className="time">{formatTimeLeft()}</div>

      <div>
        <label>小時: </label>
        <input 
          type="number" 
          value={hours} 
          onChange={handleHoursChange} 
          disabled={isActive}
        />
        <label>分鐘: </label>
        <input 
          type="number" 
          value={minutes} 
          onChange={handleMinutesChange} 
          disabled={isActive}
        />
        <label>秒: </label>
        <input 
          type="number" 
          value={seconds} 
          onChange={handleSecondsChange} 
          disabled={isActive}
        />
      </div>

      <button onClick={toggleTimer}>
        {isActive ? '暫停' : '開始'}
      </button>
      <button className="reset" onClick={resetTimer}>重置</button>
    </div>
  );

  const [hours, setHours] = useState('');
  const [minutes, setMinutes] = useState('');
  const [seconds, setSeconds] = useState('');
  const [timeLeft, setTimeLeft] = useState(0); // 剩餘時間（秒）
  const [isActive, setIsActive] = useState(false);
  const audioRef = useRef(null); // 用於引用音頻

  // 倒數計時邏輯
  useEffect(() => {
    let timer = null;
    if (isActive && timeLeft > 0) {
      timer = setInterval(() => {
        setTimeLeft((prevTime) => prevTime - 1);
      }, 1000);
    } else if (timeLeft === 0 && isActive) {
      setIsActive(false); // 停止倒數

      // 播放音檔並顯示提示訊息
      if (audioRef.current) {
        audioRef.current.play()
          .then(() => {
            // 使用 setTimeout 确保音频有足够时间播放
            setTimeout(() => {
              const confirmResult = window.confirm('Time is up! Click "OK" to stop the sound.');
              if (confirmResult && audioRef.current) {
                audioRef.current.pause();  // 停止音檔
                audioRef.current.currentTime = 0;  // 重置音檔進度
              }
            }, 100); // 延迟一小段时间，确保音频播放不会被暂停打断
          })
          .catch((error) => {
            console.error("Audio playback failed: ", error);
          });
      }
    }
    return () => clearInterval(timer);
  }, [isActive, timeLeft]);

  // 計算倒數時間
  const calculateTotalSeconds = () => {
    return (parseInt(hours) || 0) * 3600 + (parseInt(minutes) || 0) * 60 + (parseInt(seconds) || 0);
  };

  // 開始或暫停倒數
  const toggleTimer = () => {
    if (!isActive) {
      setTimeLeft(calculateTotalSeconds());
    }
    setIsActive(!isActive);
  };

  // 重置倒數時間
  const resetTimer = () => {
    setIsActive(false);
    setTimeLeft(calculateTotalSeconds());
    setHours(''); // 重置時間顯示
    setMinutes('');
    setSeconds('');
  };

  // 處理小時輸入變化
  const handleHoursChange = (e) => {
    const value = e.target.value;
    if (value === '' || (Number(value) >= 0 && Number(value) <= 23)) {
      setHours(value);
    } else {
      alert('Hours should be between 0 and 23');
      setHours('');
    }
  };

  // 處理分鐘輸入變化
  const handleMinutesChange = (e) => {
    const value = e.target.value;
    if (value === '' || (Number(value) >= 0 && Number(value) <= 59)) {
      setMinutes(value);
    } else {
      alert('Minutes should be between 0 and 59');
      setMinutes('');
    }
  };

  // 處理秒數輸入變化
  const handleSecondsChange = (e) => {
    const value = e.target.value;
    if (value === '' || (Number(value) >= 0 && Number(value) <= 59)) {
      setSeconds(value);
    } else {
      alert('Seconds should be between 0 and 59');
      setSeconds('');
    }
  };

  // 顯示剩餘時間
  const formatTimeLeft = () => {
    const hrs = Math.floor(timeLeft / 3600);
    const mins = Math.floor((timeLeft % 3600) / 60);
    const secs = timeLeft % 60;
    return `${hrs.toString().padStart(2, '0')}:${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="App">
      <h1>Countdown Timer</h1>
      <div>
        <label>Hours: </label>
        <input 
          type="number" 
          value={hours} 
          onChange={handleHoursChange} 
          onBlur={() => setHours(hours || '0')} // 當失去焦點時將空欄位設置為0
          disabled={isActive}
        />
        <label>Minutes: </label>
        <input 
          type="number" 
          value={minutes} 
          onChange={handleMinutesChange} 
          onBlur={() => setMinutes(minutes || '0')} // 當失去焦點時將空欄位設置為0
          disabled={isActive}
        />
        <label>Seconds: </label>
        <input 
          type="number" 
          value={seconds} 
          onChange={handleSecondsChange} 
          onBlur={() => setSeconds(seconds || '0')} // 當失去焦點時將空欄位設置為0
          disabled={isActive}
        />
      </div>
      <div className="time">{formatTimeLeft()}</div>
      <button onClick={toggleTimer}>
        {isActive ? 'Pause' : 'Start'}
      </button>
      <button onClick={resetTimer}>Reset</button>

      {/* 引入音檔 */}
      <audio ref={audioRef} src="/alarm.mp3" preload="auto" />
    </div>
  );
}

export default App;
